#include <stdio.h>
#include <stdlib.h>
#include <iostream>
#include <vector>
#include <string>
#include <algorithm>
using namespace std;

    char findTheDifference(string s, string t) {
        std::sort(s.begin(), s.end());
        std::sort(t.begin(), t.end());
		printf("%s\n", s.c_str());
		printf("%s\n", t.c_str());
        char added = '0';
        for(int i = 0; i < min(s.size(), t.size()); i++)
            if(s[i]!=t[i]) {added=t[i]; break;}
        added = added == '0' ? t.back(): added;
        return added;
    }
    char findTheDifference2(string s, string t) {
        std::vector<int> dic(26);
        for(char& c: s) dic[c-'a']++;
        for(char& c: t) dic[c-'a']--;
        char added = '0';
        for(int i = 0; i < 26; i++) if(dic[i]!=0) added=i+'a';
        return added;
    }
    char findTheDifference1(string s, string t) {
    std::vector<int> alpha(27);
    for(auto& c: s) {
        alpha[c-'a']++;
    }
    
    for(auto& c: t) {
        if(alpha[c-'a'] == 0) {
            return(c);
        }
        alpha[c-'a']--;
    }
    return('0');
    }
	
int main() {
	std::string t = "";
	std::string s = "";
	char c = findTheDifference(t, s);
	printf(">>%c\n", c);
	
	
	
	return 0;
}
